import React from 'react';

const EducationCard = ({ education }) => {
  return (
    <div className="card mt-4 shadow-sm">
      <div className="card-body d-flex justify-content-between align-items-start">
        <div>
          <h4 className="card-title">{education.institution}</h4>
          <h5 className="text-muted">{education.degree}</h5>
          <h6 className="text-muted mb-2">{education.duration}</h6>
          <p><strong>GPA: </strong>{education.gpa}</p>
          <p><strong>Societies: </strong>{education.societies.join(', ')}</p>
        </div>
      </div>
    </div>
  );
};

export default EducationCard;
