import React, { useState, useEffect } from 'react';
import SoftwareCard from '../components/SoftwareCard';
import GameCard from '../components/GameCard';

import softwareProjectsData from '../data/software_projects.json';
import gameProjectsData from '../data/game_projects.json';

import '../assets/projects.css';

const Projects = () => {
  const [activeTab, setActiveTab] = useState('software');
  const [featuredGameIndex, setFeaturedGameIndex] = useState(0);
  const featuredGames = gameProjectsData.filter(game => game.featured);

  useEffect(() => {
    const interval = setInterval(() => {
      setFeaturedGameIndex(prev => (prev + 1) % featuredGames.length);
    }, 4000);

    return () => clearInterval(interval);
  }, [featuredGames.length]);

  const nextGame = () => {
    setFeaturedGameIndex(prev => (prev + 1) % featuredGames.length);
  };

  const prevGame = () => {
    setFeaturedGameIndex(prev => (prev - 1 + featuredGames.length) % featuredGames.length);
  };

  const goToGame = (index) => {
    setFeaturedGameIndex(index);
  };

  return (
    <div className="container mt-5">
      <ul className="nav nav-tabs nav-justified" id="projectsTab" role="tablist">
        <li className="nav-item" role="presentation">
          <button 
            className={`nav-link software-tab ${activeTab === 'software' ? 'active' : ''}`}
            onClick={() => setActiveTab('software')}
            type="button"
          >
            <i className="bi bi-laptop me-1"></i> Software Projects
          </button>
        </li>
        <li className="nav-item" role="presentation">
          <button 
            className={`nav-link game-tab ${activeTab === 'game' ? 'active' : ''}`}
            onClick={() => setActiveTab('game')}
            type="button"
          >
            <i className="bi bi-controller me-1"></i> Game Projects
          </button>
        </li>
      </ul>

      <div className="tab-content mt-3">
        {/* Software Projects */}
        {activeTab === 'software' && (
          <div className="tab-pane fade show active">
            <h3 className="text-center">Software Projects</h3>
            {softwareProjectsData.map(project => (
              <SoftwareCard key={project.id} project={project} />
            ))}
          </div>
        )}

        {/* Game Projects */}
        {activeTab === 'game' && (
          <div className="tab-pane fade show active">
            <h3 className="text-center">Game Projects</h3>
            
            {/* Featured Game Carousel */}
            <div className="card mt-4 shadow-sm mb-4 featured-game-card">
              <div className="card-body text-center position-relative">
                <button 
                  className="btn btn-outline-primary position-absolute top-50 start-0 translate-middle-y"
                  style={{ zIndex: 2 }}
                  onClick={prevGame}
                  aria-label="Previous Game"
                >
                  <i className="bi bi-chevron-left"></i>
                </button>
                
                <div className="featured-game-content fade-featured d-inline-block">
                  {featuredGames.length > 0 && (
                    <>
                      <img 
                        src={featuredGames[featuredGameIndex].image} 
                        alt={`${featuredGames[featuredGameIndex].title} Title Card`}
                        className="featured-img img-fluid rounded mb-3"
                      />
                      <h4 className="card-title">{featuredGames[featuredGameIndex].title}</h4>
                      <p className="card-text">{featuredGames[featuredGameIndex].description}</p>
                      {featuredGames[featuredGameIndex].links && featuredGames[featuredGameIndex].links.length > 0 && (
                        <a 
                          href={featuredGames[featuredGameIndex].links.find(link => link.type === 'web')?.url || featuredGames[featuredGameIndex].links[0].url}
                          className="btn btn-outline-success" 
                          target="_blank"
                          rel="noopener noreferrer"
                        >
                          {featuredGames[featuredGameIndex].links.find(link => link.type === 'web')?.title || featuredGames[featuredGameIndex].links[0].title}
                        </a>
                      )}
                    </>
                  )}
                </div>
                
                <button 
                  className="btn btn-outline-primary position-absolute top-50 end-0 translate-middle-y"
                  style={{ zIndex: 2 }}
                  onClick={nextGame}
                  aria-label="Next Game"
                >
                  <i className="bi bi-chevron-right"></i>
                </button>
                
                <div className="carousel-dots mt-3">
                  {featuredGames.map((_, index) => (
                    <span
                      key={index}
                      className={`carousel-dot ${index === featuredGameIndex ? 'active' : ''}`}
                      onClick={() => goToGame(index)}
                      style={{
                        display: 'inline-block',
                        width: '12px',
                        height: '12px',
                        margin: '0 4px',
                        borderRadius: '50%',
                        background: index === featuredGameIndex ? '#4682b4' : '#cce6ff',
                        cursor: 'pointer',
                        border: '1px solid #4682b4',
                        boxShadow: index === featuredGameIndex ? '0 0 0 2px #084298' : 'none'
                      }}
                    />
                  ))}
                </div>
              </div>
            </div>

            <div className="alert alert-info text-center mt-4" role="alert">
              <i className="bi bi-apple me-2"></i>
              <strong>Heads up!</strong> macOS and iOS versions are <u>coming soon</u>. Stay tuned!
            </div>

            {gameProjectsData.map(game => (
              <GameCard key={game.id} game={game} />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Projects;
