import React from 'react';

const CertificationCard = ({ certification }) => {
  return (
    <div className="cert-card">
      <h4>{certification.title}</h4>
      <h5 className="text-muted">Issued By: {certification.issuer}</h5>
      <h6 className="text-muted mb-2">Date Issued: {certification.dateIssued}</h6>
      <p><strong>Credential ID:</strong> {certification.credentialId}</p>
      <p>
        <strong>Certificate Link:</strong>
        <a href={certification.link} target="_blank" rel="noopener noreferrer">
          View Certificate
        </a>
      </p>
    </div>
  );
};

export default CertificationCard;
