import React from 'react';
import '../assets/home.css';

const Home = () => {
  return (
    <div className="container mt-5 pt-5">
      <h1 className="text-center anek-devanagari-font">KARTHIK NANDAKUMAR</h1>
      <h4 className="text-center anek-devanagari-font">
        Senior at North Carolina State University - College of Engineering
      </h4>
      <h4 className="text-center anek-devanagari-font">
        Project Intern at SunTec Business Solutions
      </h4>

      <div className="profile-image-container">
        <img 
          src="/photos/ImageID_56098236_14.jpg" 
          alt="Karthik Nandakumar" 
          id="profile-image"
          onError={(e) => {
            e.target.style.display = 'none';
            console.warn('Profile image failed to load');
          }}
        />
      </div>

      {/* About Me Section as a Card */}
      <div className="card about-me-card mx-auto mt-5 shadow anek-devanagari-font">
        <div className="card-body">
          <h3 className="card-title text-center mb-3 anek-devanagari-font">About Me</h3>
          <p className="card-text text-center anek-devanagari-font">
            Computer Science senior at North Carolina State University with hands-on experience in full-stack
            web development, machine learning, and game design. Proficient in Java, Python, and C, with strong
            problem-solving, teamwork, and software lifecycle knowledge. Active participant in developer
            communities and passionate about creating engaging and efficient user-focused applications.
          </p>
        </div>
      </div>
    </div>
  );
};

export default Home;
