import React from 'react';

const VolunteeringCard = ({ volunteering }) => {
  return (
    <div className="card mt-4 shadow-sm">
      <div className="card-body d-flex justify-content-between align-items-start">
        <div>
          <h4 className="card-title">{volunteering.organization}</h4>
          <h5 className="text-muted">{volunteering.position}</h5>
          <h6 className="text-muted mb-2">Date Range: {volunteering.duration}</h6>
          <p><strong>Cause: </strong>{volunteering.cause}</p>
          <ul>
            {volunteering.responsibilities.map((responsibility, index) => (
              <li key={index}>{responsibility}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default VolunteeringCard;
