import React from 'react';

const GameCard = ({ game }) => {
  const getButtonClass = (type) => {
    switch (type) {
      case 'download':
        return 'btn-outline-primary';
      case 'web':
        return 'btn-outline-success';
      default:
        return 'btn-outline-secondary';
    }
  };

  const getButtonColor = (icon) => {
    if (icon === 'bi-android') return 'btn-outline-warning';
    if (icon === 'fa-brands fa-linux') return 'btn-outline-secondary';
    return 'btn-outline-primary';
  };

  return (
    <div className="card mt-4 shadow-sm">
      <div className="card-body">
        <img 
          src={game.image} 
          alt={`${game.title} Title Card`}
          className="img-fluid rounded mx-auto d-block mb-3" 
          style={{ maxWidth: '350px' }}
        />
        <h4 className="card-title">{game.title}</h4>
        <h6 className="text-muted mb-2">Worked on: {game.duration}</h6>
        <p className="card-text">{game.description}</p>
        <p><strong>Skills: </strong>{game.skills.join(' · ')}</p>

        {game.contributors && game.contributors.length > 0 && (
          <>
            <p><strong>Contributors:</strong></p>
            <ul>
              {game.contributors.map((contributor, index) => (
                <li key={index}>
                  {contributor.linkedin ? (
                    <a href={contributor.linkedin} target="_blank" rel="noopener noreferrer">
                      {contributor.name}
                    </a>
                  ) : (
                    contributor.name
                  )}
                </li>
              ))}
            </ul>
          </>
        )}

        {game.links && game.links.length > 0 && (
          <div className="mt-3 row justify-content-center gy-3">
            {game.links.map((link, index) => {
              let buttonClass = getButtonClass(link.type);
              if (link.icon === 'bi-android') buttonClass = 'btn-outline-warning';
              if (link.icon === 'fa-brands fa-linux') buttonClass = 'btn-outline-secondary';
              
              return (
                <div key={index} className="col-12 col-md-auto">
                  <a 
                    href={link.url}
                    className={`btn ${buttonClass} w-100 w-md-auto`}
                    target="_blank"
                    rel="noopener noreferrer"
                    download={link.type === 'download' ? true : undefined}
                  >
                    <i className={link.icon}></i> {link.title}
                  </a>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
};

export default GameCard;
