import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';
import '../assets/header.css';

const Header = () => {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  useEffect(() => {
    // Google Translate Element Initialization
    let script = null;
    
    const initGoogleTranslate = () => {
      if (!window.google || !window.google.translate) {
        script = document.createElement('script');
        script.src = '//translate.google.com/translate_a/element.js?cb=googleTranslateElementInit';
        script.async = true;
        script.onerror = () => console.warn('Google Translate failed to load');
        document.body.appendChild(script);

        window.googleTranslateElementInit = () => {
          try {
            new window.google.translate.TranslateElement({
              pageLanguage: 'en',
              layout: window.google.translate.TranslateElement.InlineLayout.SIMPLE
            }, 'google_translate_element');
          } catch (error) {
            console.warn('Google Translate initialization failed:', error);
          }
        };
      }
    };

    // Delay initialization to avoid conflicts
    const timer = setTimeout(initGoogleTranslate, 1000);

    return () => {
      clearTimeout(timer);
      if (script && document.body.contains(script)) {
        document.body.removeChild(script);
      }
    };
  }, []);

  return (
    <nav className={`navbar navbar-expand-lg custom-navbar anek-devanagari-font ${scrolled ? 'scrolled transparent' : ''}`}>
      <div className="container-fluid">
        <NavLink className="navbar-brand fw-bold anek-devanagari-font" to="/">
          Karthik Nandakumar
        </NavLink>
        <button 
          className="navbar-toggler" 
          type="button" 
          data-bs-toggle="collapse" 
          data-bs-target="#navbarNav"
          aria-controls="navbarNav" 
          aria-expanded="false" 
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav ms-auto">
            <li className="nav-item">
              <NavLink 
                className={({ isActive }) => `nav-link anek-devanagari-font ${isActive ? 'active' : ''}`}
                to="/"
              >
                Home
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink 
                className={({ isActive }) => `nav-link anek-devanagari-font ${isActive ? 'active' : ''}`}
                to="/experiences"
              >
                Experience
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink 
                className={({ isActive }) => `nav-link anek-devanagari-font ${isActive ? 'active' : ''}`}
                to="/projects"
              >
                Projects
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink 
                className={({ isActive }) => `nav-link anek-devanagari-font ${isActive ? 'active' : ''}`}
                to="/certifications"
              >
                Certifications
              </NavLink>
            </li>
          </ul>
        </div>
        <div id="google_translate_element" className="ms-3"></div>
      </div>
    </nav>
  );
};

export default Header;
