    { code: 'zu', name: 'Zulu', flag: 'ðŸ‡¿ðŸ‡¦' }
  ];

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  useEffect(() => {
    // Google Translate Element Initialization
    const initGoogleTranslate = () => {
      // Remove any existing script
      const existingScript = document.querySelector('script[src*="translate.google.com"]');
      if (existingScript) {
        existingScript.remove();
      }

      // Add the script
      const script = document.createElement('script');
      script.type = 'text/javascript';
      script.src = 'https://translate.google.com/translate_a/element.js?cb=googleTranslateElementInit';
      
      // Define the callback function
      window.googleTranslateElementInit = function() {
        console.log('Google Translate initializing...');
        
        try {        window.googleTranslateInstance = new window.google.translate.TranslateElement({
          pageLanguage: 'en',
          autoDisplay: false,
          layout: window.google.translate.TranslateElement.InlineLayout.HORIZONTAL
        }, 'google_translate_element');
          
          console.log('Google Translate initialized successfully');
          
          // Wait for the element to be ready
          setTimeout(() => {
            const selectElement = document.querySelector('#google_translate_element select');
            if (selectElement) {
              console.log('Google Translate select element found');
              // Hide the default widget
              const widget = document.querySelector('#google_translate_element .goog-te-gadget');
              if (widget) {
                widget.style.display = 'none';
              }
            } else {
              console.warn('Google Translate select element not found');
            }
            
            // Hide any banner
            const banner = document.querySelector('.goog-te-banner-frame');
            if (banner) {
              banner.style.display = 'none';
            }
          }, 1000);
          
        } catch (error) {
          console.error('Error initializing Google Translate:', error);
        }
      };

      script.onerror = () => {
        console.error('Failed to load Google Translate script');
      };

      document.head.appendChild(script);
    };

    // Initialize after a short delay
    const timer = setTimeout(initGoogleTranslate, 500);

    return () => {
      clearTimeout(timer);
      // Clean up
      const script = document.querySelector('script[src*="translate.google.com"]');
      if (script) {
        script.remove();
      }
      if (window.googleTranslateElementInit) {
        delete window.googleTranslateElementInit;
      }
    };
  }, []);

  useEffect(() => {
    // Continuously check and remove Google Translate styling that interferes with header
    const interval = setInterval(() => {
      const body = document.body;
      if (body && body.style.top && body.style.top !== '0px') {
        body.style.top = '0';
        body.style.position = 'static';
      }
      
      // Hide Google Translate top bar
      const googleBar = document.querySelector('.goog-te-banner-frame');
      if (googleBar) {
        googleBar.style.display = 'none';
      }
    }, 500);

    return () => clearInterval(interval);
  }, []);

  // Detect current language from Google Translate cookie on page load
  useEffect(() => {
    const detectCurrentLanguage = () => {
      const cookies = document.cookie.split(';');
      const googTransCookie = cookies.find(cookie => cookie.trim().startsWith('googtrans='));
      
      if (googTransCookie) {
        const cookieValue = googTransCookie.split('=')[1];
        // Cookie format is usually /en/[target_language]
        const match = cookieValue.match(/\/en\/([a-z-]+)/);
        if (match && match[1] && match[1] !== 'en') {
          console.log('Detected translated language from cookie:', match[1]);
          setCurrentLanguage(match[1]);
        }
      }
    };

    detectCurrentLanguage();
  }, []);

  const translatePage = (langCode) => {
    console.log('Attempting to translate to:', langCode);
    
    // Use the reliable cookie-based method directly
    translatePageWithCookie(langCode);
  };

  // Alternative translation method using cookies (Google Translate's method)
  const translatePageWithCookie = (langCode) => {
    console.log('Using cookie-based translation method for:', langCode);
    
    // Set Google Translate cookie
    const cookieName = 'googtrans';
    const cookieValue = langCode === 'en' ? '/en/en' : `/en/${langCode}`;
    
    // Clear any existing translation cookies first
    document.cookie = `${cookieName}=; expires=Thu, 01 Jan 1970 00:00:00 GMT; path=/`;
    document.cookie = `${cookieName}=; expires=Thu, 01 Jan 1970 00:00:00 GMT; path=/; domain=${window.location.hostname}`;
    
    // Set new translation cookie
    document.cookie = `${cookieName}=${cookieValue}; path=/; domain=${window.location.hostname}`;
    document.cookie = `${cookieName}=${cookieValue}; path=/`;
    
    console.log('Set translation cookie:', cookieValue);
    
    // Update UI state before reload
    setCurrentLanguage(langCode);
    setTranslateOpen(false);
    setSearchTerm('');
    
    // Reload the page to apply translation
    setTimeout(() => {
      window.location.reload();
    }, 100);
  };

  const getCurrentLanguage = () => {
    return languages.find(lang => lang.code === currentLanguage) || languages[0];
  };

  const filteredLanguages = languages.filter(lang =>
    lang.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (translateOpen && !event.target.closest('.custom-translate-dropdown')) {
        setTranslateOpen(false);
        setSearchTerm(''); // Clear search when dropdown closes
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [translateOpen]);

  // Debug function to check Google Translate status
  const checkGoogleTranslateStatus = () => {
    console.log('Checking Google Translate status...');
    console.log('window.google exists:', !!window.google);
    console.log('window.google.translate exists:', !!(window.google && window.google.translate));
    
    const selectElement = document.querySelector('.goog-te-combo');
    console.log('Google Translate select element found:', !!selectElement);
    
    if (selectElement) {
      console.log('Available language options:', Array.from(selectElement.options).map(opt => opt.value));
    }
    
    const googleWidget = document.getElementById('google_translate_element');
    console.log('Google Translate widget element:', !!googleWidget);
    
    if (googleWidget) {
      console.log('Widget innerHTML:', googleWidget.innerHTML);
    }
  };

  return (
    <nav className={`modern-navbar ${scrolled ? 'scrolled' : ''}`}>
      <div className="navbar-container">
        <NavLink className="navbar-brand" to="/">
          <span className="brand-text">Karthik Nandakumar</span>
          <div className="brand-underline"></div>
        </NavLink>
        
        <button 
          className="mobile-toggle" 
          type="button" 
          onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
          aria-label="Toggle navigation"
        >
          <span className="toggle-bar"></span>
          <span className="toggle-bar"></span>
          <span className="toggle-bar"></span>
        </button>
        
        <div className={`navbar-collapse ${mobileMenuOpen ? 'show' : ''}`}>
          <ul className="navbar-nav">
            <li className="nav-item">
              <NavLink 
                className={({ isActive }) => `nav-link ${isActive ? 'active' : ''}`}
                to="/"
                onClick={() => setMobileMenuOpen(false)}
              >
                <span className="nav-text">Home</span>
                <div className="nav-indicator"></div>
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink 
                className={({ isActive }) => `nav-link ${isActive ? 'active' : ''}`}
                to="/experiences"
                onClick={() => setMobileMenuOpen(false)}
              >
                <span className="nav-text">Experiences</span>
                <div className="nav-indicator"></div>
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink 
                className={({ isActive }) => `nav-link ${isActive ? 'active' : ''}`}
                to="/projects"
                onClick={() => setMobileMenuOpen(false)}
              >
                <span className="nav-text">Projects</span>
                <div className="nav-indicator"></div>
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink 
                className={({ isActive }) => `nav-link ${isActive ? 'active' : ''}`}
                to="/certifications"
                onClick={() => setMobileMenuOpen(false)}
              >
                <span className="nav-text">Certifications</span>
                <div className="nav-indicator"></div>
              </NavLink>
            </li>
          </ul>
          
          <div className="navbar-utilities">
            <div className="custom-translate-dropdown">
              <button 
                className="translate-button"
                onClick={() => setTranslateOpen(!translateOpen)}
                aria-label="Select Language"
              >
                <span className="current-lang-flag">{getCurrentLanguage().flag}</span>
                <span className="current-lang-text">{getCurrentLanguage().name}</span>
                <span className={`dropdown-arrow ${translateOpen ? 'open' : ''}`}>â–¼</span>
              </button>
              
              {translateOpen && (
                <div className="translate-dropdown">
                  <div className="translate-search">
                    <input
                      type="text"
                      placeholder="Search languages..."
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                      className="language-search-input"
                      onClick={(e) => e.stopPropagation()}
                    />
                  </div>
                  <div className="translate-options-container">
                    {filteredLanguages.map((language) => (
                      <button
                        key={language.code}
                        className={`translate-option ${currentLanguage === language.code ? 'active' : ''}`}
                        onClick={() => translatePage(language.code)}
                      >
                        <span className="lang-flag">{language.flag}</span>
                        <span className="lang-name">{language.name}</span>
                      </button>
                    ))}
                    {filteredLanguages.length === 0 && (
                      <div className="no-languages-found">
                        No languages found
                      </div>
                    )}
                  </div>
                  <div style={{ padding: '8px', borderTop: '1px solid rgba(0,0,0,0.1)' }}>
                    <button 
                      onClick={() => {
                        checkGoogleTranslateStatus();
                        setTranslateOpen(false);
                      }}
                      style={{ 
                        fontSize: '0.7rem', 
                        padding: '4px 8px', 
                        background: 'rgba(102,126,234,0.1)',
                        border: '1px solid rgba(102,126,234,0.2)',
                        borderRadius: '4px',
                        cursor: 'pointer',
                        marginRight: '8px'
                      }}
                    >
                      Debug Info
                    </button>
                    <button 
                      onClick={() => {
                        translatePageWithCookie('es');
                        setTranslateOpen(false);
                      }}
                      style={{ 
                        fontSize: '0.7rem', 
                        padding: '4px 8px', 
                        background: 'rgba(255,165,0,0.1)',
                        border: '1px solid rgba(255,165,0,0.2)',
                        borderRadius: '4px',
                        cursor: 'pointer',
                        marginRight: '8px'
                      }}
                    >
                      Test Spanish
                    </button>
                    <button 
                      onClick={() => {
                        translatePageWithCookie('en');
                        setTranslateOpen(false);
                      }}
                      style={{ 
                        fontSize: '0.7rem', 
                        padding: '4px 8px', 
                        background: 'rgba(34,197,94,0.1)',
                        border: '1px solid rgba(34,197,94,0.2)',
                        borderRadius: '4px',
                        cursor: 'pointer'
                      }}
                    >
                      Reset English
                    </button>
                  </div>
                </div>
              )}
            </div>
            
            {/* Google Translate Element - Visible but styled to be hidden */}
            <div id="google_translate_element" style={{ 
              opacity: 0,
              height: 0,
              overflow: 'hidden',
              pointerEvents: 'none'
            }}></div>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Header;
