import React, { useState } from 'react';
import WorkCard from '../components/WorkCard';
import EducationCard from '../components/EducationCard';
import VolunteeringCard from '../components/VolunteeringCard';
import OrganizationCard from '../components/OrganizationCard';

import workData from '../data/work.json';
import educationData from '../data/education.json';
import volunteeringData from '../data/volunteering.json';
import organizationsData from '../data/organizations.json';

import '../assets/experiences.css';

const Experiences = () => {
  const [activeTab, setActiveTab] = useState('work');

  return (
    <div className="container mt-5">
      <ul className="nav nav-tabs nav-justified" id="experiencesTab" role="tablist">
        <li className="nav-item" role="presentation">
          <button 
            className={`nav-link work-tab ${activeTab === 'work' ? 'active' : ''}`}
            onClick={() => setActiveTab('work')}
            type="button"
          >
            <i className="bi bi-briefcase-fill me-1"></i> Work
          </button>
        </li>
        <li className="nav-item" role="presentation">
          <button 
            className={`nav-link education-tab ${activeTab === 'education' ? 'active' : ''}`}
            onClick={() => setActiveTab('education')}
            type="button"
          >
            <i className="bi bi-mortarboard-fill me-1"></i> Education
          </button>
        </li>
        <li className="nav-item" role="presentation">
          <button 
            className={`nav-link volunteering-tab ${activeTab === 'volunteering' ? 'active' : ''}`}
            onClick={() => setActiveTab('volunteering')}
            type="button"
          >
            <i className="bi bi-people-fill me-1"></i> Volunteering
          </button>
        </li>
        <li className="nav-item" role="presentation">
          <button 
            className={`nav-link organizations-tab ${activeTab === 'organizations' ? 'active' : ''}`}
            onClick={() => setActiveTab('organizations')}
            type="button"
          >
            <i className="bi bi-diagram-3-fill me-1"></i> Organizations
          </button>
        </li>
      </ul>

      <div className="tab-content mt-3">
        {/* Work Experience */}
        {activeTab === 'work' && (
          <div className="tab-pane fade show active">
            <h3 className="text-center">Work Experience</h3>
            {workData.map(work => (
              <WorkCard key={work.id} work={work} />
            ))}
          </div>
        )}

        {/* Education */}
        {activeTab === 'education' && (
          <div className="tab-pane fade show active">
            <h3 className="text-center">Education</h3>
            {educationData.map(education => (
              <EducationCard key={education.id} education={education} />
            ))}
          </div>
        )}

        {/* Volunteering */}
        {activeTab === 'volunteering' && (
          <div className="tab-pane fade show active">
            <h3 className="text-center">Volunteering</h3>
            {volunteeringData.map(volunteering => (
              <VolunteeringCard key={volunteering.id} volunteering={volunteering} />
            ))}
          </div>
        )}

        {/* Organizations */}
        {activeTab === 'organizations' && (
          <div className="tab-pane fade show active">
            <h3 className="text-center">Organizations</h3>
            {organizationsData.map(organization => (
              <OrganizationCard key={organization.id} organization={organization} />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Experiences;
