    { code: 'zu', name: 'Zulu', flag: 'ðŸ‡¿ðŸ‡¦' }
  ];

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  useEffect(() => {
    // Google Translate Element Initialization
    let script = null;
    
    const initGoogleTranslate = () => {
      if (!window.google || !window.google.translate) {
        script = document.createElement('script');
        script.src = '//translate.google.com/translate_a/element.js?cb=googleTranslateElementInit';
        script.async = true;
        script.onerror = () => console.warn('Google Translate failed to load');
        document.body.appendChild(script);

        window.googleTranslateElementInit = () => {
          try {
            window.googleTranslateElement = new window.google.translate.TranslateElement({
              pageLanguage: 'en',
              includedLanguages: 'af,sq,am,ar,hy,az,eu,be,bn,bs,bg,ca,ceb,ny,zh,zh-tw,co,hr,cs,da,nl,eo,et,tl,fi,fr,fy,gl,ka,de,el,gu,ht,ha,haw,iw,hi,hmn,hu,is,ig,id,ga,it,ja,jw,kn,kk,km,ko,ku,ky,lo,la,lv,lt,lb,mk,mg,ms,ml,mt,mi,mr,mn,my,ne,no,or,ps,fa,pl,pt,pa,ro,ru,sm,gd,sr,st,sn,sd,si,sk,sl,so,es,su,sw,sv,tg,ta,te,th,tr,uk,ur,ug,uz,vi,cy,xh,yi,yo,zu',
              layout: window.google.translate.TranslateElement.InlineLayout.SIMPLE,
              autoDisplay: false,
              multilanguagePage: true
            }, 'google_translate_element');
            
            console.log('Google Translate initialized successfully');
            
            // Hide the default Google Translate widget after initialization
            setTimeout(() => {
              const googleWidget = document.getElementById('google_translate_element');
              if (googleWidget) {
                const selectElement = googleWidget.querySelector('select');
                if (selectElement) {
                  selectElement.style.display = 'none';
                }
              }
              
              // Hide Google Translate top bar if it appears
              const googleBar = document.querySelector('.goog-te-banner-frame');
              if (googleBar) {
                googleBar.style.display = 'none';
              }
              
              // Prevent Google Translate from adding top margin
              const body = document.body;
              if (body && body.style.top) {
                body.style.top = '0';
                body.style.position = 'static';
              }
            }, 1000);
          } catch (error) {
            console.warn('Google Translate initialization failed:', error);
          }
        };
      }
    };

    // Delay initialization to avoid conflicts
    const timer = setTimeout(initGoogleTranslate, 1000);

    return () => {
      clearTimeout(timer);
      if (script && document.body.contains(script)) {
        document.body.removeChild(script);
      }
    };
  }, []);

  useEffect(() => {
    // Continuously check and remove Google Translate styling that interferes with header
    const interval = setInterval(() => {
      const body = document.body;
      if (body && body.style.top && body.style.top !== '0px') {
        body.style.top = '0';
        body.style.position = 'static';
      }
      
      // Hide Google Translate top bar
      const googleBar = document.querySelector('.goog-te-banner-frame');
      if (googleBar) {
        googleBar.style.display = 'none';
      }
    }, 500);

    return () => clearInterval(interval);
  }, []);

  const translatePage = (langCode) => {
    console.log('Attempting to translate to:', langCode);
    
    if (window.google && window.google.translate) {
      try {
        // Method 1: Try to find and use the Google Translate select element
        const selectElement = document.querySelector('.goog-te-combo');
        if (selectElement) {
          console.log('Found Google Translate select element');
          selectElement.value = langCode;
          selectElement.dispatchEvent(new Event('change', { bubbles: true }));
        } else {
          console.log('Google Translate select not found, trying alternative method');
          
          // Method 2: Try to trigger translation directly via Google Translate API
          setTimeout(() => {
            const iframe = document.querySelector('iframe.goog-te-menu-frame');
            if (iframe) {
              const iframeDoc = iframe.contentDocument || iframe.contentWindow.document;
              const langLink = iframeDoc.querySelector(`a[data-value="${langCode}"]`);
              if (langLink) {
                langLink.click();
              }
            } else {
              // Method 3: Use the direct API if available
              if (window.google.translate.TranslateElement) {
                const translateElement = document.getElementById('google_translate_element');
                if (translateElement && window.googleTranslateElement) {
                  // Force re-initialization with target language
                  const newElement = new window.google.translate.TranslateElement({
                    pageLanguage: 'en',
                    includedLanguages: 'af,sq,am,ar,hy,az,eu,be,bn,bs,bg,ca,ceb,ny,zh,zh-tw,co,hr,cs,da,nl,eo,et,tl,fi,fr,fy,gl,ka,de,el,gu,ht,ha,haw,iw,hi,hmn,hu,is,ig,id,ga,it,ja,jw,kn,kk,km,ko,ku,ky,lo,la,lv,lt,lb,mk,mg,ms,ml,mt,mi,mr,mn,my,ne,no,or,ps,fa,pl,pt,pa,ro,ru,sm,gd,sr,st,sn,sd,si,sk,sl,so,es,su,sw,sv,tg,ta,te,th,tr,uk,ur,ug,uz,vi,cy,xh,yi,yo,zu',
                    layout: window.google.translate.TranslateElement.InlineLayout.SIMPLE,
                    autoDisplay: false
                  }, 'google_translate_element');
                  
                  setTimeout(() => {
                    const newSelectElement = document.querySelector('.goog-te-combo');
                    if (newSelectElement) {
                      newSelectElement.value = langCode;
                      newSelectElement.dispatchEvent(new Event('change', { bubbles: true }));
                    }
                  }, 500);
                }
              }
            }
          }, 100);
        }
      } catch (error) {
        console.error('Translation error:', error);
      }
    } else {
      console.warn('Google Translate not loaded yet');
    }
    
    setCurrentLanguage(langCode);
    setTranslateOpen(false);
    setSearchTerm(''); // Clear search when language is selected
  };

  const getCurrentLanguage = () => {
    return languages.find(lang => lang.code === currentLanguage) || languages[0];
  };

  const filteredLanguages = languages.filter(lang =>
    lang.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (translateOpen && !event.target.closest('.custom-translate-dropdown')) {
        setTranslateOpen(false);
        setSearchTerm(''); // Clear search when dropdown closes
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [translateOpen]);

  // Debug function to check Google Translate status
  const checkGoogleTranslateStatus = () => {
    console.log('Checking Google Translate status...');
    console.log('window.google exists:', !!window.google);
    console.log('window.google.translate exists:', !!(window.google && window.google.translate));
    
    const selectElement = document.querySelector('.goog-te-combo');
    console.log('Google Translate select element found:', !!selectElement);
    
    if (selectElement) {
      console.log('Available language options:', Array.from(selectElement.options).map(opt => opt.value));
    }
    
    const googleWidget = document.getElementById('google_translate_element');
    console.log('Google Translate widget element:', !!googleWidget);
    
    if (googleWidget) {
      console.log('Widget innerHTML:', googleWidget.innerHTML);
    }
  };

  return (
    <nav className={`modern-navbar ${scrolled ? 'scrolled' : ''}`}>
      <div className="navbar-container">
        <NavLink className="navbar-brand" to="/">
          <span className="brand-text">Karthik Nandakumar</span>
          <div className="brand-underline"></div>
        </NavLink>
        
        <button 
          className="mobile-toggle" 
          type="button" 
          onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
          aria-label="Toggle navigation"
        >
          <span className="toggle-bar"></span>
          <span className="toggle-bar"></span>
          <span className="toggle-bar"></span>
        </button>
        
        <div className={`navbar-collapse ${mobileMenuOpen ? 'show' : ''}`}>
          <ul className="navbar-nav">
            <li className="nav-item">
              <NavLink 
                className={({ isActive }) => `nav-link ${isActive ? 'active' : ''}`}
                to="/"
                onClick={() => setMobileMenuOpen(false)}
              >
                <span className="nav-text">Home</span>
                <div className="nav-indicator"></div>
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink 
                className={({ isActive }) => `nav-link ${isActive ? 'active' : ''}`}
                to="/experiences"
                onClick={() => setMobileMenuOpen(false)}
              >
                <span className="nav-text">Experiences</span>
                <div className="nav-indicator"></div>
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink 
                className={({ isActive }) => `nav-link ${isActive ? 'active' : ''}`}
                to="/projects"
                onClick={() => setMobileMenuOpen(false)}
              >
                <span className="nav-text">Projects</span>
                <div className="nav-indicator"></div>
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink 
                className={({ isActive }) => `nav-link ${isActive ? 'active' : ''}`}
                to="/certifications"
                onClick={() => setMobileMenuOpen(false)}
              >
                <span className="nav-text">Certifications</span>
                <div className="nav-indicator"></div>
              </NavLink>
            </li>
          </ul>
          
          <div className="navbar-utilities">
            <div className="custom-translate-dropdown">
              <button 
                className="translate-button"
                onClick={() => setTranslateOpen(!translateOpen)}
                aria-label="Select Language"
              >
                <span className="current-lang-flag">{getCurrentLanguage().flag}</span>
                <span className="current-lang-text">{getCurrentLanguage().name}</span>
                <span className={`dropdown-arrow ${translateOpen ? 'open' : ''}`}>â–¼</span>
              </button>
              
              {translateOpen && (
                <div className="translate-dropdown">
                  <div className="translate-search">
                    <input
                      type="text"
                      placeholder="Search languages..."
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                      className="language-search-input"
                      onClick={(e) => e.stopPropagation()}
                    />
                  </div>
                  <div className="translate-options-container">
                    {filteredLanguages.map((language) => (
                      <button
                        key={language.code}
                        className={`translate-option ${currentLanguage === language.code ? 'active' : ''}`}
                        onClick={() => translatePage(language.code)}
                      >
                        <span className="lang-flag">{language.flag}</span>
                        <span className="lang-name">{language.name}</span>
                      </button>
                    ))}
                    {filteredLanguages.length === 0 && (
                      <div className="no-languages-found">
                        No languages found
                      </div>
                    )}
                  </div>
                </div>
              )}
            </div>
            
            {/* Hidden but functional Google Translate Element */}
            <div id="google_translate_element" style={{ 
              position: 'absolute', 
              left: '-9999px', 
              top: '-9999px',
              visibility: 'hidden',
              width: '1px',
              height: '1px',
              overflow: 'hidden'
            }}></div>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Header;
